package test;

public class conditionExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int age = 17;
		
		if(age>=18) {
			System.out.println("Eligible");
		}
		else if(age == 17) {
			System.out.println("Try next year");
		}
		else {
			System.out.println("Not Eligible");
		}
	}

}
